        beforeSubmit.push(function() {
               jq("#htmlform").validate();

                     if(jq("#migrant-wrker").find(":checkbox").prop('checked') !== true &&
                      jq("#trigger").find(":checkbox").prop('checked') !== true &&
                      jq("#not-applicable").find(":checkbox").prop('checked') !== true &&
