                    setValue('refused_to_answer.error', '');
                    }


                if (getValue('your-gender.value')==="5622") {
                    if (getValue('other-specify.value') === '') {
                        getField('other-specify.error').html(' Please specify the other gender you consider yourself').show();
                        return false;
                    }
                    else{

                     return jq("#htmlform").valid();
                    }
