       var setting = jq("#165130").find(":radio:checked").val();

       if(sexAtBirth === "90165" &&  (sexPartner === "163290" || sexPartner === "163275")){
            jq("#msm").find(":checkbox").removeAttr("disabled");
            jq("#msm").find(":checkbox").attr("checked",true);

       }
