       function checkOtherItem(trigger_field , field_to_check){
       if(trigger_field == true){
       jq("#"+ field_to_check).find(":checkbox").removeAttr("disabled");
        jq("#"+ field_to_check).find(":checkbox").attr("checked",true);
       }else{
        jq("#"+ field_to_check).find(":checkbox").attr("disabled",true);
         jq("#"+ field_to_check).find(":checkbox").attr("checked",false);
       }
       }

       function checkOtherItemIfYes(trigger_field, field_to_check){
        if(trigger_field === "1065"){
         jq("#"+ field_to_check).find(":checkbox").removeAttr("disabled");
        jq("#"+ field_to_check).find(":checkbox").attr("checked",true);

       }
       else{
         jq("#"+ field_to_check).find(":checkbox").attr("checked",false);
         jq("#"+ field_to_check).find(":checkbox").attr("disabled",true);
       }
       }


     });
    });
});
}



 function disableEnableCheckBoxes(checkedValue, field_to_enable_disable){
        if(checkedValue === true){
        jq("#"+ field_to_enable_disable).find(":checkbox").removeAttr("disabled");

       }
       else{
          jq("#"+ field_to_enable_disable).find(":checkbox").attr("checked",false);
          jq("#"+ field_to_enable_disable).find(":checkbox").attr("disabled","disabled");
       }

 }

 function disableEnableCheckBox(checkedValue, field_to_enable_disable){
        if(jq("#"+ checkedValue).find(":checkbox").prop('checked')){
        jq("#"+ field_to_enable_disable).find(":checkbox").removeAttr("disabled");

       }
       else{
          jq("#"+ field_to_enable_disable).find(":checkbox").attr("checked",false);
          jq("#"+ field_to_enable_disable).find(":checkbox").attr("disabled","disabled");
       }

 }


       function disableFn(group){
        group.children("#disabled").find("input").attr("required","false");
           group.children("#disabled").fadeTo(250,0.33);
           group.children("#disabled").find(":checkbox").attr("checked",false); //uncheck
           group.children("#disabled").find("input[type$='text']").val("");
           group.children("#disabled").find("input").attr("disabled",true);  //disable

        }
        function enableFn(group){

          group.children("#disabled").fadeTo(250,1);
          group.children("#disabled").find("input").attr("disabled",false);

        }

        function enableCheckedBox(checkboxId){
             if(jq("#" + checkboxId ).find(":checkbox").prop('checked')){
                jq("#" + checkboxId).find(":checkbox").attr("disabled",false);
            }
        }
